import { BiWorld, BiChevronDown, BiEdit } from "react-icons/bi";
import { AiOutlineShareAlt, AiFillStar } from "react-icons/ai";
import { useState } from "react";
import UserImg from "../../../../common/components/img/Img";

import AttachmentImg from "../../../../../pics/atc.png";
import Car from "../../../../../pics/car.png";
import { Modal } from "react-bootstrap";
import React from "react";
import { IoCloseCircleOutline } from "react-icons/io5";
import { Post } from "../../../../common/components/post/Post";
import { CommentUiApp } from "../../../../common/components/CommentUiApp/CommentUiApp";
import { UserUi } from "../.../../../../../common/components/UserUi/UserUi";
export const Index = () => {
  return (
    <>
      <Post />
    </>
  );
};
export const PostUser = () => {
  return (
    <>
      <div className="Announcement_iner mb-4 position-relative">
        <div className="PostUser_top_profile_div">
          <UserUi
            name="Gaurav Kaushik"
            time="5 Seconds ago"
            SubName="@Everyone"
            icon={<AiOutlineShareAlt />}
            StarIcon={<AiFillStar />}
          />
          <div className="row">
            <div className="col-12">
              <div className="PostUser_text">
                <p>
                  “Give yourself credit for the days you have made it through,
                  when you thought you couldn’t” Lorem, ipsum dolor sit amet
                  consectetur adipisicing elit. Eligendi cupiditate vel nobis
                  enim magnam, inventore ipsa saepe rerum est quidem quis
                  voluptatibus quia error voluptate ullam mollitia deserunt unde
                  recusandae.
                </p>
              </div>
            </div>
          </div>
          <EditPost />
        </div>
        <div className="post_textarea_hr">
          <hr />
        </div>
        <CommentUiApp />
      </div>
    </>
  );
};
export const UserPost = () => {
  return (
    <>
      <div className="Announcement_iner mb-4">
        <div className="UserPost_top_div">
          <UserUi
            name="Gaurav Kaushik"
            time="5 Seconds ago"
            SubName="@SEO"
            icon={<AiOutlineShareAlt />}
            StarIcon={<AiFillStar />}
          />
          <EditPost />
          <div>
            <UserImg className="UserPost_img shadow" backgroundImage={Car} />
          </div>
        </div>
        <div className="post_textarea_hr">
          <hr />
        </div>
        <CommentUiApp />
      </div>
    </>
  );
};

const EditPost = () => {
  const [modalShow, setModalShow] = React.useState(false);
  return (
    <>
      <div className="row">
        <div className="col-12">
          <div className="PostUser_post_edit">
            <BiEdit onClick={() => setModalShow(true)} />
            <MyVerticallyCenteredModal
              show={modalShow}
              onHide={() => setModalShow(false)}
            />
          </div>
        </div>
      </div>
    </>
  );
};
// modal
const MyVerticallyCenteredModal = (props) => {
  const [PostTextareaOption, setPostTextareaOption] = useState(true);
  const PostTextareaOptionOpen = () => {
    setPostTextareaOption(!PostTextareaOption);
  };
  const PostList = (props) => {
    return (
      <>
        <li>{props.list}</li>
      </>
    );
  };
  const PostListData = [
    {
      list: "Everyone",
    },
    {
      list: "design",
    },
    {
      list: "marketing",
    },
    {
      list: "development",
    },
  ];
  return (
    <>
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Body className="EditPost_top_div">
          <div className="EditPost_title">
            <p>share your thoughts</p>
          </div>
          <div className="post_textarea_hr">
            <hr />
          </div>
          <button className="EditPost_close_btn" onClick={props.onHide}>
            <IoCloseCircleOutline />
          </button>

          <div className="EditPost_main_itims">
            <div className="row">
              <div className="col-xxl-8 col-xl-8 col-lg-8 col-md-12 col-sm-12 col-12">
                <UserUi name="Gaurav Kaushik" />
                <div className="post_textarea EditPost_img_post p-0">
                  <textarea
                    className="form-control"
                    rows="3"
                    placeholder="I bought a new car"
                  ></textarea>
                </div>
              </div>
              <div className="col-xxl-4 col-xl-4 col-lg-4 col-md-12 col-sm-12 col-12">
                <div>
                  <UserImg className="EditPost_img" backgroundImage={Car} />
                </div>
              </div>
            </div>
          </div>
          <div className="post_textarea_hr">
            <hr />
          </div>
          <div className="post_textarea_option">
            <div className="row">
              <div className="col-10">
                <div className="EditPost_attachment_teat">
                  <p>Add attachment to your post</p>
                </div>
              </div>
              <div className="col-2">
                <div className="post_textarea_Attachment">
                  <UserImg
                    className="PostUser_Comment_user_icon_img"
