import { NavLink } from "react-router-dom";
import Select from "react-select";
import TopPageTitle from "../../../../common/components/topPageTitle/Index";
import { BsArrowRight } from "react-icons/bs";
import Chart from "react-apexcharts";
export const MyPerformance = () => {
  const options = [
    { value: "2021", label: "2021" },
    { value: "2019", label: "2019" },
    { value: "2018", label: "2018" },
  ];
  return (
    <>
      <div className="row">
        <div className="col-xxl-9 col-xl-9 col-lg-9 col-md-6 col-sm-5 col-5">
          <TopPageTitle Name="MyPerformance" />
        </div>
        <div className="col-xxl-3 col-xl-3 col-lg-3 col-md-6 col-sm-7 col-7 MyAttendence_next_page">
          <NavLink to="/hr/previous_leaves">
            <span>Action on Leave</span>
            <BsArrowRight />
          </NavLink>
        </div>
      </div>
      <div className="row">
        <div className="col-10">
          <div className="top_inner_title">
            <h3>Absenteeism</h3>
          </div>
        </div>
        <div className="col-2">
          <div className="dashboard_top_week_Select">
            <Select options={options} placeholder="2022" />
          </div>
        </div>
        <div>
          <Chart
            type="line"
            series={[23, 80]}
            options={{
              colors: ["#85E57D", "#2DCD1F"],
              labels: ["Office", "Home"],
              dataLabels: {
                enabled: true,
                background: {
                  enabled: true,
                  foreColor: "black",
                  opacity: 1,
                },
                dropShadow: {
                  enabled: false,
                  opacity: 1,
                },
              },
              chart: {
                height: 350,
                type: "line",
                zoom: {
                  enabled: false,
                },
              },
              dataLabels: {
                enabled: false,
              },
              stroke: {
                curve: "straight",
              },
              title: {
                text: "Product Trends by Month",
                align: "left",
              },
              grid: {
                row: {
                  colors: ["#f3f3f3", "transparent"], // takes an array which will be repeated on columns
                  opacity: 0.5,
                },
              },
              xaxis: {
                categories: [
                  "Jan",
                  "Feb",
                  "Mar",
                  "Apr",
                  "May",
                  "Jun",
                  "Jul",
                  "Aug",
                  "Sep",
                ],
              },

              legend: {
                show: true,
                position: "bottom",
                horizontalAlign: "center",
                fontSize: "15px",
                markers: {
                  width: 12,
                  height: 12,
                  radius: 0,
                  offsetX: -10,
                  offsetY: 0,
                },
                itemMargin: {
                  horizontal: 40,
                  vertical: 0,
                },
              },
            }}
          ></Chart>
        </div>
      </div>
    </>
  );
};
