import TopPageTitle from "../../../../../common/components/topPageTitle/Index";
import { BiSearch } from "react-icons/bi";
import { BsArrowLeft } from "react-icons/bs";
import { AboutProfileInput } from "../../Profile/components/AboutProfile";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import React, { Component } from "react";
import { convertFromRaw } from "draft-js";
import { Editor } from "react-draft-wysiwyg";
export const CreateNewJobs = () => {
  return (
    <>
      <div className="custom_container">
        <PageTop />

        <div className="row">
          <div className="col-8">
            <CreateNewJobsForm />
          </div>
          <div className="col-4">2</div>
        </div>
      </div>
    </>
  );
};
const PageTop = () => {
  return (
    <>
      <div className="row TotalOpenings_input_main_div g-xxl-4 g-xl-4 g-lg-4 g-md-4 g-sm-3 g-3">
        <div className="col-xxl-8 col-xl-8 col-lg-8 col-md-6 col-sm-12 col-12">
          <TopPageTitle
            TitleLink={-1}
            TilelIcon={<BsArrowLeft />}
            Name="Create New Jobs"
          />
        </div>
        <div className="col-xxl-4 col-xl-4 col-lg-4 col-md-6 col-sm-12 col-12">
          <div className="TotalOpenings_input_div">
            <span>
              <BiSearch />
            </span>
            <input
              type="email"
              className="form-control w-100"
              placeholder="Search by name, email or department"
            />
          </div>
        </div>
      </div>
    </>
  );
};
const CreateNewJobsForm = () => {
  return (
    <>
      <div className="row g-xxl-4 g-xl-4 g-lg-4 g-md-4 g-sm-3 g-3">
        <div className="col-12">
          <AboutProfileInput
            label="Job Title"
            type="text"
            className="form-control"
            placeholder="Job Title"
          />
        </div>
        <div className="col-4">
          <AboutProfileInput
            label="Job Title"
            type="text"
            className="form-control"
            placeholder="Internship"
          />
        </div>
        <div className="col-4">
          <AboutProfileInput
            label="Department"
            type="text"
            className="form-control"
            placeholder="Marketing"
          />
        </div>
        <div className="col-4">
          <AboutProfileInput
            label="Experience"
            type="text"
            className="form-control"
            placeholder="0-1 Year"
          />
        </div>
        <div className="col-12">
          <EditorConvertToHTML />
        </div>
      </div>
    </>
  );
};
const content = {
  entityMap: {},
  blocks: [
    {
      key: "637gr",
      text: "Initialized from content state.",
      type: "unstyled",
      depth: 0,
      inlineStyleRanges: [],
      entityRanges: [],
      data: {},
    },
  ],
};

class EditorConvertToJSON extends Component {
  constructor(props) {
    super(props);
    const contentState = convertFromRaw(content);
    this.state = {
      contentState,
    };
  }

  onContentStateChange: Function = (contentState) => {
    this.setState({
      contentState,
    });
  };

  render() {
    const { contentState } = this.state;
    return (
      <div>
        <Editor
          wrapperClassName="demo-wrapper"
          editorClassName="demo-editor"
          onContentStateChange={this.onContentStateChange}
        />
        <textarea disabled value={JSON.stringify(contentState, null, 4)} />
      </div>
    );
  }
}
