let Photo = "https://source.unsplash.com/random/500x500/?girl";
export const ProjectDetails = () => {
  return (
    <>
      <div className="custom_container">
        <PageAllTopTitle link={-1} icon={<BsArrowLeft />} title="Project" />
        <div className="row row g-xxl-4 g-xl-4 g-lg-4 g-md-4 g-sm-3 g-3 ">
          <div className="col-12">
            <ProjectDetailsCard />
          </div>
          <div className="col-12">
            <MyTab />
          </div>
        </div>
      </div>
    </>
  );
};
const ProjectDetailsCard = () => {
  class UserLogoUploadButton extends Component {
    handleFileUpload = (event) => {
      console.log(event.target.files[0].name);
    };

    render() {
      return (
        <>
          <input
            ref="fileInput"
            onChange={this.handleFileUpload}
            type="file"
