import { BiWorld, BiChevronDown } from "react-icons/bi";
import { CgAttachment } from "react-icons/cg";
import { useState } from "react";
export const Post = () => {
  const [PostTextareaOption, setPostTextareaOption] = useState(true);
  const PostTextareaOptionOpen = () => {
    setPostTextareaOption(!PostTextareaOption);
  };
  return (
    <>
      <div className="custom_container ">
        <div className="Announcement_iner mb-4 position-relative">
          <div className="post_textarea">
            <textarea
              className="form-control"
              rows="5"
              placeholder="How is your day today!....."
            ></textarea>
          </div>
          <div className="post_textarea_hr">
            <hr />
          </div>
          <div className="post_textarea_option">
            <div className="row">
              <div className="col-10">
                <div
                  className="post_textarea_option_iner_main_div"
                  onClick={PostTextareaOptionOpen}
                >
                  <span>
                    <BiWorld />
                  </span>
                  <p>Everyone can view</p>
                  <div>
                    <BiChevronDown />
                  </div>
                </div>
              </div>
              <div className="col-2">
                <div className="post_textarea_Attachment">
                  <CgAttachment />
                </div>
              </div>
            </div>
            <div
              className={`${
                PostTextareaOption
                  ? "PostTextareaOption"
                  : "PostTextareaOption2"
              }`}
            >
              <div className="post_textarea_option_iner_option">
                <ul>
                  <li>Everyone</li>
                  <li>Everyone</li>
                  <li>Everyone</li>
                  <li>Everyone</li>
                  {InfoCardAppData.map((val, i) => {
                    return (
                      <InfoCardApp
                        key={i}
                        backgroundcolor={val.backgroundcolor}
                        textcolor={val.textcolor}
                        cardtitle={val.cardtitle}
                        date={val.date}
                        // icon={val.icon}
                        dashbaordIcon={val.dashbaordIcon}
                        iconbackgroundcolor={val.iconbackgroundcolor}
                        url={val.url}
                      />
                    );
                  })}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
