import TopPageTitle from "../../../../common/components/topPageTitle/Index";
import { BsArrowLeft } from "react-icons/bs";
import { NavLink } from "react-router-dom";
import { Nav, Tab } from "react-bootstrap";
export const UserAppraisal = () => {
  return (
    <>
      <div className="custom_container">
        <div className="row">
          <div className="col-12">
            <TopPageTitle
              TilelIcon={<BsArrowLeft />}
              TitleLink={-1}
              Name="Roushan’s Appraisal"
            />
          </div>
        </div>
        <div className="custom_container_iner">
          <div className="UserAppraisal_top_title">
            <h3>View Appraisal</h3>
          </div>
          <UserAppraisalDate />
          <Question />
        </div>
        <Feedback />
      </div>
    </>
  );
};
const UserAppraisalDate = () => {
  const UserAppraisalDateApp = (props) => {
    return (
      <>
        <div>
          <Nav.Item>
            <NavLink to="/hr/apply_leave">{props.date}</NavLink>
          </Nav.Item>
        </div>
      </>
    );
  };
  const UserAppraisalDateData = [
    {
      date: "April 2021",
    },
    {
      date: "December 2021",
    },
    {
      date: "April 2022",
    },
  ];
  return (
    <>
      <div className="UserAppraisalDate_list_btn">
        <Tab.Container id="left-tabs-example" defaultActiveKey="first">
          <Nav variant="pills" className="flex">
            {UserAppraisalDateData.map((val, i) => {
              return <UserAppraisalDateApp key={i} date={val.date} />;
            })}
          </Nav>
        </Tab.Container>
      </div>
    </>
  );
};
const Question = () => {
  const QuestionApp = (props) => {
    return (
      <>
        <div className="Question_top_div">
          <p>
            <span>{props.number}.</span>
            {props.text}
          </p>
          <div className="ApprovalDetailsPage_date_picker">
            <textarea
              className="form-control"
              placeholder={props.placeholder}
              rows="2"
            ></textarea>
          </div>
        </div>
      </>
    );
  };
  const QuestionData = [
    {
      number: "1",
      text: "Can you observe any meaningful relationship between what you do and overall goals of the company?",
      placeholder: "yes",
    },
    {
      number: "2",
      text: "What elements of your job interest you the most?",
      placeholder:
        "In publishing and graphic design, Lorem ipsum is a placeholder text commonly used to demonstrate the visual form of a document or a typeface without relying on meaningful content. Lorem ipsum may be used as a placeholder before the final copy is available.",
    },
    {
      number: "3",
      text: "What elements of your job interest you the most?",
      placeholder:
        "In publishing and graphic design, Lorem ipsum is a placeholder text commonly used to demonstrate the visual form of a document or a typeface without relying on meaningful content. Lorem ipsum may be used as a placeholder before the final copy is available.",
    },
    {
      number: "4",
      text: "What elements of your job interest you the most?",
      placeholder:
        "In publishing and graphic design, Lorem ipsum is a placeholder text commonly used to demonstrate the visual form of a document or a typeface without relying on meaningful content. Lorem ipsum may be used as a placeholder before the final copy is available.",
    },
    {
      number: "5",
      text: "What elements of your job interest you the most?",
      placeholder:
        "In publishing and graphic design, Lorem ipsum is a placeholder text commonly used to demonstrate the visual form of a document or a typeface without relying on meaningful content. Lorem ipsum may be used as a placeholder before the final copy is available.",
    },
  ];
  return (
    <>
      {QuestionData.map((val, i) => {
        return (
          <QuestionApp
            key={i}
            number={val.number}
            text={val.text}
            placeholder={val.placeholder}
          />
        );
      })}
    </>
  );
};
const Feedback = () => {
  return (
    <>
      <h3>Feedback</h3>
    </>
  );
};
