import { NavLink } from "react-router-dom";
import Select from "react-select";
import TopPageTitle from "../../../../common/components/topPageTitle/Index";
import { BsArrowRight } from "react-icons/bs";
import Chart from "react-apexcharts";
export const MyPerformance = () => {
  const options = [
    { value: "2021", label: "2021" },
    { value: "2019", label: "2019" },
    { value: "2018", label: "2018" },
  ];
  return (
    <>
      <div className="row">
        <div className="col-xxl-9 col-xl-9 col-lg-9 col-md-6 col-sm-5 col-5">
          <TopPageTitle Name="MyPerformance" />
        </div>
        <div className="col-xxl-3 col-xl-3 col-lg-3 col-md-6 col-sm-7 col-7 MyAttendence_next_page">
          <NavLink to="/hr/previous_leaves">
            <span>Action on Leave</span>
            <BsArrowRight />
          </NavLink>
        </div>
      </div>
      <div className="row">
        <div className="col-10">
          <div className="top_inner_title">
            <h3>Absenteeism</h3>
          </div>
        </div>
        <div className="col-2">
          <div className="dashboard_top_week_Select">
            <Select options={options} placeholder="2022" />
          </div>
        </div>
        <div>
          <Chart
            type="line"
            series={[23, 80]}
            options={{
              dataLabels: {
                enabled: true,
                background: {
                  enabled: true,
                  foreColor: "black",
                  opacity: 1,
                },
                dropShadow: {
                  enabled: false,
                  opacity: 1,
                },
              },
              chart: {
                height: 350,
                type: "line",
                zoom: {
                  enabled: false,
                },
              },
              dataLabels: {
                enabled: false,
              },
              stroke: {
                curve: "straight",
              },
              title: {
                text: "Product Trends by Month",
                align: "left",
              },
              grid: {
                row: {
                  colors: ["#f3f3f3", "transparent"], // takes an array which will be repeated on columns
                  opacity: 0.5,
                },
              },
            }}
          ></Chart>
        </div>
      </div>
    </>
  );
};
class ApexChart extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      series: [
        {
          name: "Desktops",
          data: [10, 41, 35, 51, 49, 62, 69, 91, 148],
        },
      ],
      options: {
        chart: {
          height: 350,
          type: "line",
          zoom: {
            enabled: false,
          },
        },
        dataLabels: {
          enabled: false,
        },
        stroke: {
          curve: "straight",
        },
        title: {
          text: "Product Trends by Month",
          align: "left",
        },
        grid: {
          row: {
            colors: ["#f3f3f3", "transparent"], // takes an array which will be repeated on columns
            opacity: 0.5,
          },
        },
        xaxis: {
          categories: [
            "Jan",
            "Feb",
            "Mar",
            "Apr",
            "May",
            "Jun",
            "Jul",
            "Aug",
            "Sep",
          ],
        },
      },
    };
  }

  render() {
    return (
      <div id="chart">
        <ReactApexChart
          options={this.state.options}
          series={this.state.series}
          type="line"
          height={350}
        />
      </div>
    );
  }
}

const domContainer = document.querySelector("#app");
ReactDOM.render(React.createElement(ApexChart), domContainer);
