import React, { Component } from "react";
import { BiEdit } from "react-icons/bi";
import Img from "../components/img/Img";
let Photo = "https://source.unsplash.com/random/500x500/?girl";
export class ProfileImg extends Component {
  handleFileUpload = (event) => {
    console.log(event.target.files[0].name);
  };

  render() {
    return (
      <>
        <input
          ref="fileInput"
          onChange={this.handleFileUpload}
          type="file"
          style={{ display: "none" }}
          id="actual-btn"
          hidden
          // multiple={false}
        />
        <div className=" user_logo_upload_main_div">
          <button onClick={() => this.refs.fileInput.click()}>
            <BiEdit />
          </button>
          <Img backgroundImage={Photo} className="project_details_card_img" />
        </div>
        <div>
          <label htmlFor="actual-btn">Choose File</label>
        </div>
      </>
    );
  }
}
export const ProfileImg2 = () => {
