import TopPageTitle from "../../../../common/components/topPageTitle/Index";
import { BsArrowLeft, BsArrowRight } from "react-icons/bs";
import { BiCheckCircle } from "react-icons/bi";
import { MdOutlineRadioButtonChecked } from "react-icons/md";
import { RiCheckboxBlankCircleLine } from "react-icons/ri";
import { NavLink } from "react-router-dom";
import { useState } from "react";
export const Appraisal = () => {
  const steps = [<Stap1 />, <Stap2 />, <Stap3 />, <Stap4 />];
  const [currentStep, setCurrentStep] = useState(0);
  return (
    <>
      <div className="custom_container">
        <div className="row">
          <div className="col-xxl-9 col-xl-9 col-lg-9 col-md-6 col-sm-5 col-5">
            <TopPageTitle
              TilelIcon={<BsArrowLeft />}
              TitleLink={-1}
              Name="Appraisal"
            />
          </div>
          <div className="col-xxl-3 col-xl-3 col-lg-3 col-md-6 col-sm-7 col-7 MyAttendence_next_page">
            <NavLink to="/">
              <span>Previous Appraisal</span>
              <BsArrowRight />
            </NavLink>
          </div>
        </div>
        <div className="Appraisal_progress_bar_custom_container">
          <div>
            <progress className="Appraisal_progress_bar" value="32" max="100">
              32%
            </progress>
            <div className="Appraisal_progress_text_bar">
              <ul>
                <li>
                  <span>
                    <BiCheckCircle />
                  </span>
                  Step 1
                </li>
                <li>
                  <span>
                    <MdOutlineRadioButtonChecked />
                  </span>
                  Step 2
                </li>
                <li>
                  <span>
                    <RiCheckboxBlankCircleLine />
                  </span>
                  Step 3
                </li>
                <li>
                  <span>
                    <RiCheckboxBlankCircleLine />
                  </span>
                  Step 4
                </li>
              </ul>
            </div>
          </div>
          <div>{steps[currentStep]}</div>
          <div>
            <div className="IssueAssets_btn_bottom_div IssueAssets_btn_bottom_div2 mt-0">
              <div>
                <button
                  className="IssueAssets_btn3"
                  onClick={() => {
                    setCurrentStep((old) => old - 1);
                  }}
                  disabled={!currentStep}
                >
                  Previous
                </button>
              </div>
              <div>
                <div>
                  <button
                    className="IssueAssets_btn2"
                    onClick={() => {
                      setCurrentStep((old) => old + 1);
                    }}
                  >
                    Next
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

const Stap1 = () => {
  return (
    <>
      <h3>stap 1</h3>
    </>
  );
};
const Stap2 = () => {
  return (
    <>
      <h3>stap 2</h3>
    </>
  );
};
const Stap3 = () => {
  return (
    <>
      <h3>stap 3</h3>
    </>
  );
};
const Stap4 = () => {
  return (
    <>
      <h3>stap 4</h3>
    </>
  );
};
