import TopPageTitle from "../../../../../common/components/topPageTitle/Index";
import { BsArrowLeft } from "react-icons/bs";
import { BiSearch, BiDotsVerticalRounded } from "react-icons/bi";
import Select from "react-select";
import { Nav, Tab } from "react-bootstrap";
import React from "react";
import Slider from "react-slick";
import { NavLink } from "react-router-dom";
import { useState } from "react";
export const Interviews = () => {
  return (
    <>
      <div className="custom_container">
        <PageTop />
        <InterviewsTabs />
      </div>
    </>
  );
};
const PageTop = () => {
  const options = [
    { value: "designer", label: "designer" },
    { value: "developer", label: "developer" },
  ];
  return (
    <>
      <div>
        <div className="row TotalOpenings_input_main_div g-xxl-4 g-xl-4 g-lg-4 g-md-4 g-sm-3 g-3">
          <div className="col-xxl-3 col-xl-3 col-lg-3 col-md-3 col-sm-12 col-12">
            <TopPageTitle
              TitleLink={-1}
              TilelIcon={<BsArrowLeft />}
              Name="Interviews"
            />
          </div>
          <div className="col-xxl-7 col-xl-7 col-lg-7 col-md-6 col-sm-12 col-12">
            <div className="TotalOpenings_input_div">
              <span>
                <BiSearch />
              </span>
              <input
                type="email"
                className="form-control"
                placeholder="Search by name, email or department"
              />
            </div>
          </div>
          <div className="col-xxl-2 col-xl-2 col-lg-2 col-md-3 col-sm-12 col-12">
            <div className="dashboard_top_week_Select">
              <Select options={options} placeholder="All Department" />
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
const InterviewsTabs = () => {
  return (
    <>
      <div className="profile_tab_div">
        <Tab.Container id="left-tabs-example" defaultActiveKey="first">
          <Nav variant="pills" className="flex">
            <Nav.Item>
              <Nav.Link eventKey="first">Upcoming</Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="second">Completed</Nav.Link>
            </Nav.Item>
          </Nav>

          <Tab.Content>
            <Tab.Pane eventKey="first">
              <div className="m_t"></div>
              <MySlider />
            </Tab.Pane>
            <Tab.Pane eventKey="second">
              <div className="m_t"></div>2
            </Tab.Pane>
          </Tab.Content>
        </Tab.Container>
      </div>
    </>
  );
};
const MySlider = () => {
  var settings = {
    dots: false,
    infinite: false,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
    initialSlide: 0,

    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,
        },
      },
      {
        breakpoint: 576,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };
  return (
    <>
      <Slider {...settings}>
        <div>
          <JobCards />
        </div>
        <div>
          <JobCards />
        </div>
        <div>
          <JobCards />
        </div>
        <div>
          <JobCards />
        </div>
        <div>
          <JobCards />
        </div>
      </Slider>
    </>
  );
};
const JobCards = () => {
  const JobCardsApp = (props) => {
    const [JobCardsLists, setJobCardsLists] = useState(false);
    const JobCardsListsOpen = () => {
      setJobCardsLists(!JobCardsLists);
    };
    return (
      <>
        <div className="JobCards_main_div MySlider_card">
          <div className="row">
            <div className="col-3">
              <NavLink to="/">
                <div
                  className="JobCards_main_div_round"
                  style={{ backgroundColor: props.color }}
                ></div>
              </NavLink>
            </div>
            <div className="col-6">
              <NavLink to="/">
                <h3>{props.name}</h3>
                <p>{props.applicationsNumber}</p>
              </NavLink>
            </div>
            <div className="col-1">
              <div className="JobCards_main_div_menu">
                <BiDotsVerticalRounded
                  style={{ color: props.color }}
                  onClick={JobCardsListsOpen}
                />
                {JobCardsLists ? <JobCardsList /> : null}
              </div>
            </div>
          </div>
        </div>
      </>
    );
  };
  const JobCardsData = [
    {
      name: "HR Manager",
      applicationsNumber: "12 Applications",
      color: "#F1C21B",
    },
  ];
  return (
    <>
      {JobCardsData.map((val, i) => {
        return (
          <JobCardsApp
            key={i}
            name={val.name}
            applicationsNumber={val.applicationsNumber}
            color={val.color}
          />
        );
      })}
    </>
  );
};
const JobCardsList = () => {
  return (
    <>
      <div className="JobCards_main_list_div">
        <ul>
          <NavLink to="/hr/create_new_jobs">
            <li>elit</li>
          </NavLink>
          <li>close</li>
        </ul>
      </div>
    </>
  );
};
