import { BsArrowLeft } from "react-icons/bs";
import Input from "../../../../common/components/input/Input";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";
import { AiOutlinePlus } from "react-icons/ai";
import React from "react";
import { FiChevronDown } from "react-icons/fi";
import TopPageTitle from "../../../../common/components/topPageTitle/Index";

export const PreviousLeaves = (props) => {
  const [userBreks, setUserBreks] = useState(true);

  const changNameUser = () => {
    setUserBreks(!userBreks);
  };
  const ReimbursementData = [
    {
      EID: "01",
      From: "Sick Leave",
      ItemName: "03",
      date: "05/25/2021",
      EComment: "NA",
      to: "10/07/2021",
    },
    {
      EID: "01",
      From: "Sick Leave",
      ItemName: "03",
      date: "05/25/2021",
      EComment: "NA",
      to: "10/07/2021",
    },
    {
      EID: "01",
      From: "Sick Leave",
      ItemName: "03",
      date: "05/25/2021",
      EComment: "NA",
      to: "10/07/2021",
    },
    {
      EID: "01",
      From: "Sick Leave",
      ItemName: "03",
      date: "05/25/2021",
      EComment: "NA",
      to: "10/07/2021",
    },
    {
      EID: "01",
      From: "Sick Leave",
      ItemName: "03",
      date: "05/25/2021",
      EComment: "NA",
      to: "10/07/2021",
    },
    {
      EID: "01",
      From: "Sick Leave",
      ItemName: "03",
      date: "05/25/2021",
      EComment: "NA",
      to: "10/07/2021",
    },
    {
      EID: "01",
      From: "Sick Leave",
      ItemName: "03",
      date: "05/25/2021",
      EComment: "NA",
      to: "10/07/2021",
    },
    {
      EID: "01",
      From: "Sick Leave",
      ItemName: "03",
      date: "05/25/2021",
      EComment: "NA",
      to: "10/07/2021",
    },
    {
      EID: "01",
      From: "Sick Leave",
      ItemName: "03",
      date: "05/25/2021",
      EComment: "NA",
      to: "10/07/2021",
    },
    {
      EID: "01",
      From: "Sick Leave",
      ItemName: "03",
      date: "05/25/2021",
      EComment: "NA",
      to: "10/07/2021",
    },
  ];
  const ReimbursementApp = (props) => {
    const [userApprove, setUserApprove] = useState(true);
    const [userBreks, setUserBreks] = useState(true);

    const changNameUser = () => {
      setUserBreks(!userBreks);
    };
    const changApprove = () => {
      setUserApprove(!userApprove);
    };
    return (
      <>
        <tr className="ApprovalDetailsPage_thead_td">
          <td>{props.EID}</td>
          <td>{props.From}</td>
          <td>{props.ItemName}</td>
          <td>{props.date}</td>
          <td>{props.to}</td>
          <td>{props.EComment}</td>
          <td>
            <div
              className={`${
                userBreks ? "add_remarks_title" : "add_remarks_title2"
              }`}
            >
              <div className="add_remarks_title" onClick={changNameUser}>
                Add Remarks
                <span>
                  <AiOutlinePlus />
                </span>
              </div>
            </div>
            <div
              className={`${userBreks ? "AddRemarksRemover" : "AddRemarks"}`}
            >
              <div className="add_remarks_input_div">
