import { Nav, Tab } from "react-bootstrap";
import TopPageTitle from "../../../../common/components/topPageTitle/Index";
import { NavLink } from "react-router-dom";
import { BsArrowLeft } from "react-icons/bs";
import Input from "../../../../common/components/input/Input";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";
import { FiPlusCircle } from "react-icons/fi";
import React from "react";
import { FiChevronDown } from "react-icons/fi";
import Select from "react-select";
export const PayrollIndex = () => {
  return (
    <>
      <div className="profile_tab_div">
        <Tab.Container id="left-tabs-example" defaultActiveKey="first">
          <Tab.Content>
            <Tab.Pane eventKey="first">
              <TopPageTitle Name="Salary" />
            </Tab.Pane>
            <Tab.Pane eventKey="second">
              <TopPageTitle Name="Attendence" />
            </Tab.Pane>
          </Tab.Content>
          <Nav variant="pills" className="flex">
            <Nav.Item>
              <Nav.Link eventKey="first">Salary</Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="second">Attendence</Nav.Link>
            </Nav.Item>
          </Nav>

          <Tab.Content>
            <Tab.Pane eventKey="first">
              <SalaryList />
            </Tab.Pane>
            <Tab.Pane eventKey="second">2</Tab.Pane>
          </Tab.Content>
        </Tab.Container>
      </div>
    </>
  );
};
const SalaryList = () => {
  const [userBreks, setUserBreks] = useState(true);

  const changNameUser = () => {
    setUserBreks(!userBreks);
  };
  const LeaveData = [
    {
      EID: "01",
      EmployeeName: "Roushan Kumar",
      LeaveType: "Sick Leave",
      RemainingLeaves: "05",
      From: "10/05/2021",
      To: "10/07/2021",
    },
    {
      EID: "01",
      EmployeeName: "Roushan Kumar",
      LeaveType: "Sick Leave",
      RemainingLeaves: "05",
      From: "10/05/2021",
      To: "10/07/2021",
    },
    {
      EID: "01",
      EmployeeName: "Roushan Kumar",
      LeaveType: "Sick Leave",
      RemainingLeaves: "05",
      From: "10/05/2021",
      To: "10/07/2021",
    },
    {
      EID: "01",
      EmployeeName: "Roushan Kumar",
      LeaveType: "Sick Leave",
      RemainingLeaves: "05",
      From: "10/05/2021",
      To: "10/07/2021",
    },
    {
      EID: "01",
      EmployeeName: "Roushan Kumar",
