import * as React from "react";
import IconButton from "@mui/material/IconButton";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import { BsReply } from "react-icons/bs";
import { AiOutlineShareAlt } from "react-icons/ai";
import { MdContentCopy } from "react-icons/md";
import { RiDeleteBinLine } from "react-icons/ri";
import { useNavigate } from "react-router-dom";
import { CustomizedMenus } from "./ChatSubMenu";
import { Dropdown, ButtonToolbar } from "rsuite";
import Badge from "@mui/material/Badge";
const options = [
  { link: "/", name: "Reply", icon: <BsReply /> },
  { link: "/", name: "Forward", icon: <AiOutlineShareAlt /> },
  { link: "/", name: "Copy", icon: <MdContentCopy /> },
  { link: "/", name: "Delete", icon: <RiDeleteBinLine /> },
];

const ITEM_HEIGHT = 30;

export const LongMenuw = () => {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };
  const navigate = useNavigate();

  return (
    <div className="chat_option">
      <IconButton
        aria-label="more"
        id="long-button"
        aria-controls={open ? "long-menu" : undefined}
        aria-expanded={open ? "true" : undefined}
        aria-haspopup="true"
        onClick={handleClick}
      >
        <MoreVertIcon />
      </IconButton>
      <Menu
        id="long-menu"
        MenuListProps={{
          "aria-labelledby": "long-button",
        }}
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        PaperProps={{
          style: {
            maxHeight: ITEM_HEIGHT * 4.5,
            width: "15ch",
          },
        }}
      >
        {options.map((option) => (
          <MenuItem
            key={option.name}
            selected={option === "Pyxis"}
            onClick={handleClose}
          >
            <div
              className="chat_option_name_div"
              onClick={() => navigate(option.link)}
            >
              {option.name}
              <span className="icon">{option.icon}</span>
            </div>
          </MenuItem>
        ))}
        <div onClick={handleClose} className="sub_menu_to">
          <CustomizedMenus />
        </div>
      </Menu>
    </div>
  );
};

export const LongMenu = () => {
  return (
    <div>
      <ButtonToolbar>
        <Dropdown title="Dropdown" activeKey="e-2">
          <Dropdown.Item eventKey="a">Item A</Dropdown.Item>
          <Dropdown.Item eventKey="b">Item B</Dropdown.Item>
          <Dropdown.Item eventKey="c">Item C</Dropdown.Item>
          <Dropdown.Item eventKey="d">Item D</Dropdown.Item>
          <Dropdown.Menu title="Active Menu">
            <Dropdown.Item eventKey="e-1">Item E-1</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </ButtonToolbar>
    </div>
  );
};
