                })}
              </AvatarGroup>
            </div>
          </td>
          <td>
            <div className="Participants_main_div_tr">{props.date}</div>
          </td>
          <td>
            <div className="mt-1">
              <div className="progress_all_option_icon">
                <ProjectListDropDown />
              </div>
            </div>
          </td>
        </tr>
      </>
    );
