import { NavLink } from "react-router-dom";
import Select from "react-select";
import TopPageTitle from "../../../../common/components/topPageTitle/Index";
import { BsArrowRight } from "react-icons/bs";
import Chart from "react-apexcharts";
export const MyPerformance = () => {
  const options = [
    { value: "2021", label: "2021" },
    { value: "2019", label: "2019" },
    { value: "2018", label: "2018" },
  ];
  return (
    <>
      <div className="row">
        <div className="col-xxl-9 col-xl-9 col-lg-9 col-md-6 col-sm-5 col-5">
          <TopPageTitle Name="MyPerformance" />
        </div>
        <div className="col-xxl-3 col-xl-3 col-lg-3 col-md-6 col-sm-7 col-7 MyAttendence_next_page">
          <NavLink to="/hr/previous_leaves">
            <span>Action on Leave</span>
            <BsArrowRight />
          </NavLink>
        </div>
      </div>
      <div className="row">
        <div className="col-10">
          <div className="top_inner_title">
            <h3>Absenteeism</h3>
          </div>
        </div>
        <div className="col-2">
          <div className="dashboard_top_week_Select">
            <Select options={options} placeholder="2022" />
          </div>
        </div>
        <div></div>
      </div>
      <div className="row g-xxl-4 g-xl-4 g-lg-4 g-md-4 g-sm-3 g-3">
        <div className="col-4">
          <div className="MyPerformance_chart_div">1</div>
        </div>
        <div className="col-8">
          <div className="MyPerformance_chart_div">
            <Chart
              type="line"
              series={[
                {
                  name: "Desktops",
                  data: [10, 41, 35, 51, 49, 62, 69, 91, 148],
                },
              ]}
              options={{
                colors: ["#85E57D", "#2DCD1F"],
                labels: ["Office", "Home"],
                chart: {
                  height: 350,
                  type: "line",
                  zoom: {
                    enabled: false,
                  },
                },
                dataLabels: {
                  enabled: false,
                },
                stroke: {
                  curve: "straight",
                },
                title: {
                  text: "Product Trends by Month",
                  align: "left",
                },
                legend: {
                  show: true,
                  position: "top",
                  horizontalAlign: "center",
                  fontSize: "15px",
                  markers: {
                    width: 12,
                    height: 12,
                    radius: 0,
                    offsetX: -10,
                    offsetY: 0,
                  },
                  itemMargin: {
                    horizontal: 40,
                    vertical: 0,
                  },
                },
              }}
            ></Chart>
          </div>
        </div>
      </div>
    </>
  );
};
