import Select from "react-select";
import { AiOutlineClose } from "react-icons/ai";
import TopPageTitle from "../../../../../common/components/topPageTitle/Index";
import { AboutProfileInput } from "../../Profile/components/AboutProfile";
import Input from "../../../../../common/components/input/Input";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import { Modal } from "react-bootstrap";
import Logo from "../../../../../common/components/img/Img";
import GifPhoto from "../../../../../../pics/su.gif";
import React from "react";

import { BsArrowLeft, BsCaretDownFill, BsCaretUpFill } from "react-icons/bs";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
export const AddDepartment = () => {
  return (
    <>
      <div className="custom_container">
        <PageTop />
        <div className="row g-xxl-4 g-xl-4 g-lg-4 g-md-4 g-sm-3 g-3">
          <div className="col-12">
            <div className="custom_container_iner">
              <div className="AddDepartment_main_div">
                <div className="AddDepartment_inner_div">
                  <AddOrRemoveAsset />
                </div>
              </div>
            </div>
          </div>
          <div className="col-12">
            <NewApplicationTabel />
          </div>
        </div>
      </div>
    </>
  );
};
const PageTop = () => {
  return (
    <>
      <div>
        <div className="row TotalOpenings_input_main_div ">
          <div className="col-12">
            <TopPageTitle
              TitleLink={-1}
              TilelIcon={<BsArrowLeft />}
              Name="Add Department"
            />
          </div>
        </div>
      </div>
    </>
  );
};
const AddOrRemoveAsset = () => {
  const [modalShow1, setModalShow1] = React.useState(false);
  return (
    <>
      <div className="row g-xxl-4 g-xl-4 g-lg-4 g-md-4 g-sm-3 g-3">
        <div className="col-12">
          <AboutProfileInput
            label="Create department name"
            type="text"
            className="form-control"
            placeholder="Create a new department"
          />
        </div>
        <div className="col-12">
          <div className="document_verification_app_btn_div text-center">
            <button
              onClick={() => setModalShow1(true)}
              className=" w-auto px-4"
            >
              Add
            </button>
          </div>
          <Success show={modalShow1} onHide={() => setModalShow1(false)} />
        </div>
      </div>
    </>
  );
};
const NewApplicationTabel = () => {
  const MissingInformationData = [
    {
      Name: "sunny charkhwal",
      date: "05/25/2021",
      Availability: "Immediate",
      Experience: "5 Years",
      Expectation: "6 LPA",
    },
    {
      Name: "sunny charkhwal",
      date: "05/25/2021",
      Availability: "Immediate",
      Experience: "5 Years",
      Expectation: "6 LPA",
    },
    {
      Name: "sunny charkhwal",
      date: "05/25/2021",
      Availability: "Immediate",
      Experience: "5 Years",
      Expectation: "6 LPA",
    },
    {
      Name: "sunny charkhwal",
      date: "05/25/2021",
      Availability: "Immediate",
      Experience: "5 Years",
      Expectation: "6 LPA",
    },
    {
      Name: "sunny charkhwal",
      date: "05/25/2021",
      Availability: "Immediate",
      Experience: "5 Years",
      Expectation: "6 LPA",
    },
    {
      Name: "sunny charkhwal",
      date: "05/25/2021",
      Availability: "Immediate",
      Experience: "5 Years",
      Expectation: "6 LPA",
    },
    {
      Name: "sunny charkhwal",
      date: "05/25/2021",
      Availability: "Immediate",
      Experience: "5 Years",
      Expectation: "6 LPA",
    },
  ];
  const MissingInformationApp = (props) => {
    const navigate = useNavigate();
    const [NewApplicationTabelName, setNewApplicationTabelName] =
      useState(true);
    const NewApplicationTabelNameChang = () => {
      setNewApplicationTabelName(!NewApplicationTabelName);
    };

    return (
      <>
        <tr
          className="ApprovalDetailsPage_thead_td"
          style={{ verticalAlign: "middle" }}
        >
          <td className="MissingInformation_td d-flex  align-items-center">
            <span
              onMouseOver={NewApplicationTabelNameChang}
              onMouseOut={NewApplicationTabelNameChang}
            >
              {NewApplicationTabelName ? (
                <Test1 text="s" />
              ) : (
                <Test1 text="+4" />
              )}
            </span>
            {props.Name}
          </td>
          <td className="MissingInformation_td">{props.date}</td>
          <td className="MissingInformation_td">{props.Availability}</td>
          <td className="MissingInformation_td">{props.Experience}</td>
          <td className="MissingInformation_td">{props.Expectation}</td>
          <td>
            <button
              onClick={() => navigate("/hr/design_application")}
              className="missing_information_td_button"
            >
              View
            </button>
          </td>
        </tr>
      </>
    );
  };
  const options = [
    { value: " Developers", label: " Developers" },
    { value: "Graphic Designer", label: "Graphic Designer" },
  ];
  return (
    <>
      <div>
        <div className="custom_container_iner">
          <div className="row g-3 mb-4">
            <div className="col-xxl-2 col-xl-2 col-lg-2 col-md-6 col-sm-6 col-6">
              <div className="ApprovalDetailsPage_date_picker">
                <label>From</label>
                <Input type="date" className="form-control" />
              </div>
            </div>
            <div className="col-xxl-2 col-xl-2 col-lg-2 col-md-6 col-sm-6 col-6">
              <div className="ApprovalDetailsPage_date_picker">
                <label>to</label>
                <Input type="date" className="form-control" />
              </div>
            </div>
            <div className="col-xxl-2 col-xl-2 col-lg-3 col-md-12 col-sm-12 col-12 dep_all">
              <div className="dashboard_top_week_Select">
                <Select options={options} placeholder="all Departments" />
              </div>
            </div>
            <div className="col-xxl-2 col-xl-2 col-lg-2 col-md-12 col-sm-12 col-12 dep_all">
              <div className="dashboard_top_week_Select">
                <Select options={options} placeholder="all round" />
              </div>
            </div>
            <div className="col-xxl-4 col-xl-4 col-lg-3 col-md-12 col-sm-12 col-12 ApprovalDetailsPage_search_input">
              <div className="ApprovalDetailsPage_date_picker">
                <div className="ApprovalDetailsPage_search_input_iner_div">
                  <span>
                    <FontAwesomeIcon icon={faMagnifyingGlass} />
                  </span>
                  <Input
                    type="text"
                    className="form-control"
                    placeholder="Search"
                  />
                </div>
              </div>
            </div>
          </div>
          <div className="ApprovalDetailsPage_top_div pt-0">
            <table className="table table-hover MissingInformation_main_div text-center text-capitalize">
              <thead>
                <tr className="ApprovalDetailsPage_thead_tr">
                  <th
                    scope="col"
                    className=" text-start"
                    style={{ paddingLeft: "5rem" }}
                  >
                    <span className="new_application_table_filter_div">
                      Name
                      <span className="new_application_table_filter">
                        <BsCaretUpFill />
                        <BsCaretDownFill />
                      </span>
                    </span>
                  </th>
                  <th scope="col">
                    <span className="new_application_table_filter_div">
                      Date
                      <span className="new_application_table_filter">
                        <BsCaretUpFill />
                        <BsCaretDownFill />
                      </span>
                    </span>
                  </th>
                  <th scope="col">
                    <span className="new_application_table_filter_div">
                      Availability
                      <span className="new_application_table_filter">
                        <BsCaretUpFill />
                        <BsCaretDownFill />
                      </span>
                    </span>
                  </th>
                  <th scope="col">
                    <span className="new_application_table_filter_div">
                      Experience
                      <span className="new_application_table_filter">
                        <BsCaretUpFill />
                        <BsCaretDownFill />
                      </span>
                    </span>
                  </th>
                  <th scope="col">
                    <span className="new_application_table_filter_div">
                      Expectation
                      <span className="new_application_table_filter">
                        <BsCaretUpFill />
                        <BsCaretDownFill />
                      </span>
                    </span>
                  </th>
                  <th scope="col">Resume</th>
                </tr>
              </thead>
              <tbody>
                {MissingInformationData.map((val, i) => {
                  return (
                    <MissingInformationApp
                      key={i}
                      Name={val.Name}
                      date={val.date}
                      Availability={val.Availability}
                      Experience={val.Experience}
                      Expectation={val.Expectation}
                    />
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </>
  );
};
const AddedAssetModal = (props) => {
  return (
    <Modal
      {...props}
      size="md"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Body className="UploadDocumentModal_body bg-white">
        <div className="UploadDocumentModal_body_close_btn">
          <span onClick={props.onHide}>
            <AiOutlineClose />
          </span>
        </div>
        <div className="row g-xxl-4 g-xl-4 g-lg-4 g-md-4 g-sm-3 g-3">
          <div className="col-12">
            <AboutProfileInput
              label="Rename Name Asset Name"
              type="text"
              className="form-control"
              placeholder="Add Asset Name"
            />
          </div>
        </div>
        <div className="UploadDocumentModal_body_btn_div">
          <button
            className="UploadDocumentModal_body_btn"
            onClick={props.onHide}
          >
            save
          </button>
        </div>
      </Modal.Body>
    </Modal>
  );
};

const Success = (props) => {
  return (
    <Modal
      {...props}
      size="md"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Body className="UploadDocumentModal_body bg-white">
        <div className="UploadDocumentModal_body_close_btn">
          <span onClick={props.onHide}>
            <AiOutlineClose />
          </span>
        </div>

        <div>
          <Logo className="success_gif" backgroundImage={GifPhoto} />
        </div>
        <p className="UploadDocumentModal_body_title text-center">Success</p>
        <div className="UploadDocumentModal_body_btn_div">
          <button
            className="UploadDocumentModal_body_btn"
            onClick={props.onHide}
          >
            ok
          </button>
        </div>
      </Modal.Body>
    </Modal>
  );
};
